// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

contract HotelBooking {
    // (Room)  
    struct Room {
        uint id;          //   
        string name;      //  
        uint price;       //   (Wei )
        bool isBooked;    //   
    }

    // (Booking)  
    struct Booking {
        address customer;     //    
        uint roomId;          //   ID
        uint checkInDate;     //   ()
        uint checkOutDate;    //   ()
        uint bookingTime;     //   ()
        bool isCancelled;     //   
        string ipfsHash;      //   IPFS 
    }

    address public owner;           //  
    Room[] public rooms;            //   
    Booking[] public bookings;      //   

    //  ID    
    mapping(uint => Booking) public roomBookings;

    //     
    event RoomBooked(uint roomId, address customer, uint checkInDate, uint checkOutDate);

    //     
    event BookingCancelled(uint bookingId, address customer, uint refundAmount);

    //     
    constructor() {
        owner = msg.sender;
    }

    //       
    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can perform this action");
        _;
    }

    //    ,   
    function addRoom(string memory _name, uint _price) public onlyOwner {
        uint roomId = rooms.length; //   ID    
        rooms.push(Room(roomId, _name, _price, false)); //    
    }

    //   
    function bookRoom(uint _roomId, uint _checkInDate, uint _checkOutDate, string memory _ipfsHash) public payable {
        require(_roomId < rooms.length, "Invalid room id"); //  ID  
        require(!rooms[_roomId].isBooked, "Room is already booked"); //      
        require(msg.value == rooms[_roomId].price, "Incorrect Ether sent"); //    

        rooms[_roomId].isBooked = true; //    
        uint bookingId = bookings.length; //   ID    
        bookings.push(Booking(msg.sender, _roomId, _checkInDate, _checkOutDate, block.timestamp, false, _ipfsHash)); //    
        roomBookings[_roomId] = bookings[bookingId]; //  ID   

        emit RoomBooked(_roomId, msg.sender, _checkInDate, _checkOutDate); // RoomBooked  
    }

    //   
    function cancelBooking(uint _roomId) public {
        Booking storage booking = roomBookings[_roomId]; //    
        require(booking.customer == msg.sender, "Only the customer can cancel the booking"); //      
        require(!booking.isCancelled, "Booking is already cancelled"); //     

        uint refundAmount = calculateRefund(booking.bookingTime, booking.roomId); //   
        booking.isCancelled = true; //    
        rooms[_roomId].isBooked = false; //     

        payable(msg.sender).transfer(refundAmount); //    
        emit BookingCancelled(_roomId, msg.sender, refundAmount); // BookingCancelled  
    }

    //        
    function calculateRefund(uint bookingTime, uint roomId) internal view returns (uint) {
        uint elapsed = block.timestamp - bookingTime; //     
        uint price = rooms[roomId].price; //   

        //      
        if (elapsed < 1 minutes) {
            return price * 100 / 100; // 1    100% 
        } else if (elapsed < 2 minutes) {
            return price * 90 / 100; // 2    90% 
        } else if (elapsed < 3 minutes) {
            return price * 80 / 100; // 3    80% 
        } else if (elapsed < 4 minutes) {
            return price * 70 / 100; // 4    70% 
        } else if (elapsed < 5 minutes) {
            return price * 60 / 100; // 5    60% 
        } else {
            return 0; // 5     
        }
    }

    //     
    function getRooms() public view returns (Room[] memory) {
        return rooms; //   
    }

    //     
    function getBookings() public view returns (Booking[] memory) {
        return bookings; //   
    }

    //      
    function getBookingsLength() public view returns (uint) {
        return bookings.length;
    }
}
